mapper.select:
SELECT TPC.CLEAN_PAY_ID, TPC.NORMAL_PAY_ID, TPC.CLEAN_AMT, TPC.DATA_FLAG, SEL.PARAM_NAME AS DATA_FLAG_NAME, TP.CNT_NUM, TP.SUSPENSE_DATE, TP.PROVIDER_CODE, TP.PROVIDER_NAME, TP.INVOICE_AMT, TP.PAY_AMT, TP.SUSPENSE_AMT, TP.SUS_TOTAL_AMT FROM TD_PAY_CLEAN TPC LEFT JOIN TD_PAY TP ON TPC.NORMAL_PAY_ID = TP.PAY_ID LEFT JOIN SYS_SELECT SEL ON SEL.CATEGORY_ID = 'CLEAN_DATA_FLAG' AND SEL.PARAM_VALUE = TPC.DATA_FLAG WHERE (TPC.DATA_FLAG = '02' OR TPC.DATA_FLAG='03' )
AND TPC.CLEAN_PAY_ID = #{cleanPayId ,jdbcType=VARCHAR}
AND TP.PAY_ID = #{payId ,jdbcType=VARCHAR}
AND TP.CNT_NUM LIKE '%' || #{cntNum ,jdbcType=VARCHAR} || '%'
AND TP.CNT_TYPE = #{cntType ,jdbcType=VARCHAR}
AND TP.PROVIDER_NAME LIKE '%' || #{providerName,jdbcType=VARCHAR} || '%'
AND TP.SUSPENSE_DATE >= #{befDate ,jdbcType=VARCHAR}
AND TP.SUSPENSE_DATE <= #{aftDate ,jdbcType=VARCHAR}
ORDER BY TPC.CLEAN_PAY_ID DESC

mapper.update:
UPDATE TD_PAY_CLEAN  SET 
DATA_FLAG=#{dataFlag,jdbcType=VARCHAR}
WHERE  CLEAN_PAY_ID=#{cleanPayId,jdbcType=VARCHAR}
AND (DATA_FLAG='02' OR  DATA_FLAG='03')

UPDATE TD_PAY_CLEAN  SET 
DATA_FLAG=#{dataFlag,jdbcType=VARCHAR}
WHERE  CLEAN_PAY_ID=#{cleanPayId,jdbcType=VARCHAR}
AND (DATA_FLAG='02' OR DATA_FLAG='03')
